    let res = await axios.post('http://localhost:3003/api/', {
      startDate: selectedStartDate,
      endDate: selectedEndDate,
      type: value,
      topn: parseInt(topN)
    })
    // console.log(res)
    setQueryResult(res)
    console.log(queryResult)
    for(let i =0; i < Object.keys(queryResult.data.Month).length;i++){
      xtry.push(queryResult.data.count[i]);
    }
  }
  const[ydata] =useState(xtry);
  const svgRef = useRef();

  useEffect(() => {
    // setup svg container
    const svg = d3.select(svgRef.current),
      margin = 200,
      width = svg.attr("width") - margin,
      height = svg.attr("height") - margin

    svg.append("text")
      .attr("transform", "translate(100,0)")
      .attr("x", width/4)
      .attr("y", 50)
      .attr("font-size", "24px")
      .text("Crime in Neighbourhood by Months")

    // set scaling
    var xScale = d3.scaleBand().range([0, width]).padding(0.4),
        yScale = d3.scaleLinear().range([height, 0]);

    var g = svg.append("g")
        .attr("transform", "translate(" + 100 + "," + 100 + ")");


      xScale.domain(ydata.map((val,i) => i));
