      .call(d3.axisBottom(xScale).tickFormat(function(d){return d+1;}))
      .append("text")
      .attr("y", height - 250)
      .attr("x", width/2)
      .attr("text-anchor", "end")
      .attr("stroke", "black")
      .text("Month");

      g.append("g")
         .call(d3.axisLeft(yScale).tickFormat(function(d){
             return d;
         })
         .ticks(15))
         .append("text")
         .attr("transform", "rotate(-90)")
         .attr("x",0-height/4)
         .attr("dy", "-5.1em")
         .attr("text-anchor", "end")
         .attr("stroke", "black")
         .text("Amount of Crime");

        g.selectAll(".bar")
         .data(ydata)
         .enter().append("rect")
         .attr("class", "bar")
         .attr("x", (v,i) => xScale(i))
         .attr("y", yScale)
         .attr("width", xScale.bandwidth())
         .attr("height", val => height - yScale(val));
      } ,[]);


